import React, {ChangeEvent, KeyboardEvent, useState} from 'react';
import {FilterValuesType} from './App';
import {AddItemForm} from "./AddItemForm";
import {EditableSpan} from "./EditableSpan";
import {Button} from "@material-ui/core";

export type TaskType = {
    id: string
    title: string
    isDone: boolean
}

type TodolistPropsType = {
    title: string
    tasks: Array<TaskType>
    removeTask: (taskId: string, todoListID: string) => void
    changeFilter: (value: FilterValuesType, todoListID: string) => void
    addTask: (title: string, todoListID: string) => void
    changeTaskStatus: (id: string, isDone: boolean, todoListID: string) => void
    filter: FilterValuesType
    todoListID: string
    removeTodoList: (todoListID: string) => void
    changeTaskTitle: (taskID: string, newTitle: string, todoListID: string) => void
    changeTodoListTitle: (title: string, todoListID: string) => void
}

export function Todolist(props: TodolistPropsType) {

    const onClickRemoveTodoList = () => props.removeTodoList(props.todoListID);

    const onAllClickHandler = () => props.changeFilter("all", props.todoListID);
    const onActiveClickHandler = () => props.changeFilter("active", props.todoListID);
    const onCompletedClickHandler = () => props.changeFilter("completed", props.todoListID);

    const addTask = (title: string) => props.addTask(title, props.todoListID);

    const changeTodoListTilte = (title: string) => props.changeTodoListTitle(title, props.todoListID);

    return <div>
        <h3>
            <EditableSpan title={props.title} changeTitle={changeTodoListTilte}/>
            <button onClick={onClickRemoveTodoList}>x</button>
        </h3>
        <AddItemForm addItem={addTask}/>
        <ul>
            {
                props.tasks.map(t => {
                    const onClickHandler = () => props.removeTask(t.id, props.todoListID)
                    const onChangeHandler = (e: ChangeEvent<HTMLInputElement>) => {
                        let newIsDoneValue = e.currentTarget.checked;
                        props.changeTaskStatus(t.id, newIsDoneValue, props.todoListID);
                    }

                    const changeTaskTitle = (title: string) => {
                        props.changeTaskTitle(t.id, title, props.todoListID);
                    }

                    return <li key={t.id} className={t.isDone ? "is-done" : ""}>
                        <input type="checkbox" onChange={onChangeHandler} checked={t.isDone}/>
                        <EditableSpan title={t.title} changeTitle={changeTaskTitle} />
                        <button onClick={onClickHandler}>x</button>
                    </li>
                })
            }
        </ul>
        <div>
            <Button color={"primary"} size={"small"} variant={"contained"} className={props.filter === "all" ? "active-filter" : ""} onClick={onAllClickHandler}>All</Button>
            <Button color={"primary"} size={"small"} variant={"contained"} className={props.filter === "active" ? "active-filter" : ""} onClick={onActiveClickHandler}>Active
            </Button>
            <Button color={"primary"} size={"small"} variant={"contained"} className={props.filter === "completed" ? "active-filter" : ""}
                    onClick={onCompletedClickHandler}>Completed
            </Button>
        </div>
    </div>
}
